INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (212, 24, 'Posibilidad de perforación o de punzonamiento', NULL, 'Heridas punzantes, lesiones en manos y pies, perforaciones en abdomen o tórax, infecciones por objetos contaminados.', 'Perforación de órganos vitales, sepsis, amputación, tétanos, muerte por hemorragia.', '{"EMO": 1}'::jsonb, '["Capacidad de mantener atención en tareas de precisión", "Coordinación motora fina adecuada", "Buena agudeza visual", "Capacidad de seguir protocolos de seguridad estrictamente"]'::jsonb, '["Trastornos de la coordinación motora", "Deficiencias visuales no corregidas", "Neuropatías periféricas que afecten sensibilidad en manos", "Trastorno por déficit de atención severo"]'::jsonb, '["Guantes de protección anticorte (nivel 3-5 según EN 388)", "Calzado de seguridad con plantilla antipunzón", "Gafas de seguridad", "Delantales resistentes a perforación", "Mangas protectoras"]'::jsonb, '{"eliminacion": "Eliminar herramientas punzantes innecesarias, automatizar procesos con objetos punzocortantes.", "sustitucion": "Utilizar herramientas con puntas romas cuando sea posible, materiales menos peligrosos.", "controles_ingenieria": "Protecciones en herramientas punzantes, contenedores para desecho de agujas/objetos punzantes (cortopunzantes), mesas de trabajo con superficies anti-deslizantes.", "controles_administrativos": "Capacitación en manejo seguro de herramientas punzocortantes, procedimientos de trabajo seguro, inspecciones periódicas de EPP, programa de vacunación (tétanos), primeros auxilios disponibles."}'::jsonb, false, 43, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (213, 24, 'Posibilidad de corte o seccionamiento', NULL, 'Laceraciones, cortes profundos, hemorragias, daño en tendones, nervios y vasos sanguíneos, amputaciones.', 'Amputación de dedos o miembros, sección de arterias con hemorragia masiva, muerte por desangramiento, discapacidad permanente.', '{"EMO": 1}'::jsonb, '["Coordinación motora fina y gruesa adecuada", "Buena agudeza visual", "Capacidad de concentración sostenida", "Reflejos y tiempo de reacción normales"]'::jsonb, '["Trastornos de la coordinación motora", "Deficiencias visuales severas no corregidas", "Neuropatías que afecten sensibilidad táctil", "Epilepsia no controlada", "Trastorno por déficit de atención no tratado"]'::jsonb, '["Guantes anticorte certificados (nivel 5 EN 388 para alto riesgo)", "Mangas y delantales anticorte", "Gafas de seguridad o pantalla facial", "Calzado de seguridad", "Protectores de antebrazo"]'::jsonb, '{"eliminacion": "Automatizar procesos de corte, eliminar herramientas manuales de corte cuando sea posible.", "sustitucion": "Utilizar herramientas de corte con sistemas de seguridad (retracción automática de cuchillas, corte por láser, etc.).", "controles_ingenieria": "Guardas de seguridad en máquinas cortantes, sistemas de paro de emergencia, mesas de corte con guías, contenedores seguros para almacenamiento de herramientas cortantes.", "controles_administrativos": "Procedimientos de trabajo seguro (lockout/tagout), capacitación en uso correcto de herramientas cortantes, mantenimiento preventivo de maquinaria, inspección periódica de equipos de protección, señalización de áreas de riesgo, botiquín equipado para emergencias."}'::jsonb, false, 44, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (214, 27, 'Almacenamiento inadecuado', NULL, 'Caída de objetos almacenados, golpes, atrapamiento, colapso de estanterías, bloqueo de vías de evacuación, incendios.', 'Aplastamiento por colapso de estanterías, lesiones graves por caída de objetos pesados, imposibilidad de evacuación en emergencias, muerte.', '{"EMO": 1, "PSM": 2}'::jsonb, '["Capacidad de organización espacial", "Conocimiento de principios de almacenamiento seguro", "Capacidad física para manipular cargas", "Capacidad de seguir procedimientos de orden y aseo"]'::jsonb, '["Trastorno obsesivo-compulsivo de acumulación severo", "Discapacidades cognitivas que impidan seguir procedimientos de organización"]'::jsonb, '["Calzado de seguridad", "Casco de seguridad (en áreas de almacenamiento en altura)", "Guantes de trabajo", "Faja lumbar ergonómica (para manipulación de cargas)"]'::jsonb, '{"eliminacion": "Eliminar almacenamiento innecesario mediante gestión just-in-time, reducir inventarios.", "sustitucion": "Sustituir almacenamiento vertical excesivo por almacenamiento horizontal más seguro.", "controles_ingenieria": "Estanterías ancladas a pared o piso, sistemas de retención para evitar caída de objetos, pasillos de ancho adecuado (mínimo 1.2m), señalización de capacidad máxima de carga, sistemas anti-vuelco en estanterías móviles.", "controles_administrativos": "Procedimiento de almacenamiento seguro (objetos pesados abajo, livianos arriba), inspecciones periódicas de estanterías, programa de orden y aseo (5S), capacitación en almacenamiento seguro, señalización de cargas máximas, prohibición de almacenar objetos que obstruyan salidas de emergencia, cumplimiento NTC 1461 (almacenamiento)."}'::jsonb, true, 45, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (215, 27, 'Condiciones del piso', NULL, 'Caídas al mismo nivel, resbalones, tropiezos, torceduras, esguinces, fracturas, traumatismos craneoencefálicos.', 'Fractura de cadera (especialmente en adultos mayores), traumatismo craneoencefálico grave, lesión de columna vertebral, muerte por caída.', '{"EMO": 1, "OPTO": 2}'::jsonb, '["Buena agudeza visual para detectar irregularidades en el piso", "Equilibrio y coordinación adecuados", "Capacidad de marcha normal", "Atención y concentración para evitar distracciones"]'::jsonb, '["Vértigo crónico o trastornos del equilibrio severos", "Deficiencias visuales severas no corregidas", "Ataxias o trastornos de la marcha", "Neuropatías periféricas que afecten sensibilidad en pies", "Uso de medicamentos que causen mareos o somnolencia"]'::jsonb, '["Calzado antideslizante certificado (SRC según EN ISO 20345)", "Calzado cerrado con suela de agarre adecuado", "Bastones o apoyos para personas con dificultades de movilidad"]'::jsonb, '{"eliminacion": "Eliminar desniveles innecesarios, reparar grietas y huecos en el piso.", "sustitucion": "Reemplazar pisos lisos y resbaladizos por superficies antideslizantes.", "controles_ingenieria": "Pisos antideslizantes (coeficiente de fricción ≥0.5), rampas con pendiente adecuada (<12%), señalización de desniveles, iluminación adecuada (mínimo 200 lux en pasillos), drenajes para evitar encharcamientos, tapetes antideslizantes en zonas húmedas.", "controles_administrativos": "Programa de mantenimiento preventivo de pisos, limpieza inmediata de derrames, señalización de pisos mojados (''Piso mojado'' visible), restricción de tránsito durante limpieza, inspecciones periódicas de condiciones de pisos, capacitación en prevención de caídas, uso de calzado apropiado obligatorio."}'::jsonb, true, 46, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (216, 27, 'Escaleras y barandas inadecuadas o mal estado', NULL, 'Caídas de altura, caídas por escaleras, fracturas, traumatismos, esguinces, lesiones de columna.', 'Caída mortal desde escaleras, fractura de columna vertebral con paraplejia, traumatismo craneoencefálico severo, muerte.', '{"EMO": 1, "PST": 2, "OPTO": 1}'::jsonb, '["Buena agudeza visual", "Equilibrio y coordinación adecuados", "Ausencia de vértigo o miedo a alturas", "Capacidad de marcha y movilidad normales", "Fuerza en miembros inferiores adecuada"]'::jsonb, '["Vértigo o miedo a alturas (acrofobia)", "Trastornos del equilibrio severos", "Artritis severa en miembros inferiores", "Deficiencias visuales severas no corregidas", "Enfermedades cardiovasculares que causen mareos", "Neuropatías que afecten movilidad o sensibilidad en piernas"]'::jsonb, '["Calzado antideslizante con buen agarre", "Calzado cerrado (no sandalias ni zapatos abiertos)", "Uso de barandas obligatorio (tomar con la mano)"]'::jsonb, '{"eliminacion": "Eliminar escaleras innecesarias, instalar rampas cuando sea posible, usar ascensores/montacargas para transporte de cargas.", "sustitucion": "Reemplazar escaleras deterioradas o con diseño inadecuado.", "controles_ingenieria": "Escaleras con barandas a ambos lados (altura 90-105 cm según NSR-10), pasamanos continuos, huella mínima 25 cm y contrahuella máxima 18 cm, superficie antideslizante en escalones, iluminación adecuada (mínimo 150 lux), señalización de primer y último escalón, barandas resistentes (carga mínima 100 kg), descansos cada 12 escalones máximo.", "controles_administrativos": "Inspecciones periódicas de escaleras y barandas, mantenimiento correctivo inmediato de daños, señalización de escaleras en mal estado (prohibición de uso), prohibición de correr en escaleras, campaña de concientización sobre uso de barandas, restricción de carga manual en escaleras (usar montacargas), cumplimiento NSR-10 Título J (escaleras)."}'::jsonb, true, 47, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (217, 16, 'Humedad Relativa (Vapor de agua)', NULL, 'Enfermedades respiratorias (asma, bronquitis), alergias, dermatitis, proliferación de moho y ácaros, fatiga, malestar térmico.', 'Neumonitis por hipersensibilidad (pulmón del granjero), asma ocupacional severo, infecciones fúngicas pulmonares (aspergilosis), empeoramiento de enfermedades respiratorias crónicas.', '{"EMO": 1}'::jsonb, '["Función respiratoria normal", "Ausencia de alergias severas a moho o ácaros", "Piel sin dermatitis crónica severa"]'::jsonb, '["Asma no controlada", "Enfermedad Pulmonar Obstructiva Crónica (EPOC)", "Alergias severas a moho, hongos o ácaros", "Dermatitis atópica severa", "Fibrosis quística", "Inmunodeficiencias"]'::jsonb, '["Respirador N95 (en ambientes con moho visible)", "Ropa de trabajo transpirable", "Guantes impermeables (si hay contacto con superficies húmedas)", "Calzado impermeable"]'::jsonb, '{"eliminacion": "Eliminar fuentes de humedad innecesarias (filtraciones, fugas de agua).", "sustitucion": "Procesos en seco cuando sea posible, materiales resistentes a la humedad.", "controles_ingenieria": "Ventilación adecuada (natural o mecánica), deshumidificadores en áreas críticas, sistemas de drenaje efectivos, impermeabilización de techos y paredes, mantenimiento de niveles de humedad relativa entre 30-60% (ASHRAE), extracción localizada en fuentes de vapor.", "controles_administrativos": "Monitoreo periódico de humedad relativa (higrómetros), mantenimiento preventivo de sistemas de ventilación, limpieza y desinfección regular de áreas húmedas, reparación inmediata de filtraciones, inspecciones de moho, rotación de personal en áreas de alta humedad, pausas en ambientes secos, educación sobre síntomas de alergias respiratorias."}'::jsonb, false, 19, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (218, 19, 'Manipulación de alimentos', NULL, 'Enfermedades transmitidas por alimentos (ETAs), infecciones gastrointestinales, hepatitis A, parasitosis, dermatitis por contacto con alimentos.', 'Intoxicación alimentaria severa (salmonelosis, E. coli O157:H7), hepatitis A, síndrome urémico hemolítico, deshidratación severa, hospitalización, muerte (en casos extremos).', '{"EMO": 1}'::jsonb, '["Buenas prácticas de higiene personal", "Capacidad de seguir estrictamente protocolos de manipulación de alimentos", "Ausencia de enfermedades infecciosas transmisibles", "Conocimiento de cadena de frío y temperatura segura de alimentos"]'::jsonb, '["Enfermedades gastrointestinales activas (diarrea, vómito)", "Infecciones de piel en manos (heridas abiertas, dermatitis infectada)", "Tuberculosis activa", "Hepatitis A en fase aguda", "Portadores de Salmonella, Shigella o E. coli patógena", "Condiciones que impidan lavado de manos frecuente"]'::jsonb, '["Guantes desechables (nitrilo o látex)", "Gorro o cofia", "Tapabocas (obligatorio al manipular alimentos listos para consumo)", "Delantal limpio e impermeable", "Calzado cerrado antideslizante"]'::jsonb, '{"eliminacion": "Automatizar procesos de manipulación de alimentos cuando sea posible.", "sustitucion": "Utilizar alimentos pre-procesados que requieran menos manipulación.", "controles_ingenieria": "Superficies de trabajo lisas, no porosas y lavables (acero inoxidable), lavamanos exclusivos con agua potable, jabón líquido y toallas desechables, sistemas de refrigeración adecuados, separación física entre áreas sucias y limpias, control de plagas, ventilación adecuada.", "controles_administrativos": "Capacitación obligatoria en manipulación higiénica de alimentos (curso BPM), exámenes médicos ocupacionales periódicos (coproscópico, frotis faríngeo), vacunación (hepatitis A, fiebre tifoidea), lavado de manos obligatorio (antes de iniciar, después de ir al baño, después de tocar basura), prohibición de usar joyas, uñas pintadas o largas, control de temperatura de alimentos (refrigeración <5°C, cocción >74°C), cumplimiento Resolución 2674 de 2013 (BPM para alimentos)."}'::jsonb, true, 34, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (219, 29, 'Sin disponibilidad de agua potable', 'SAN-AGUA-POTABLE', 'Enfermedades gastrointestinales (diarrea, cólera, fiebre tifoidea), parasitosis, hepatitis A, deshidratación, dermatitis.', 'Cólera con deshidratación severa, fiebre tifoidea, hepatitis A, muerte por deshidratación o infección severa.', '{"EMO": 1}'::jsonb, '["Capacidad de identificar fuentes seguras de agua", "Conocimiento de métodos de purificación básicos", "Habilidad para reportar falta de suministro de agua potable"]'::jsonb, '["Inmunodeficiencias severas", "Enfermedades gastrointestinales crónicas que se agraven con diarrea", "Diabetes descontrolada (vulnerable a deshidratación)", "Insuficiencia renal (riesgo con agua de mala calidad)"]'::jsonb, '["Guantes desechables para manipulación de agua no potable", "Recipientes limpios y cubiertos para almacenamiento de agua", "Gel antibacterial (sin agua) para higiene de manos"]'::jsonb, '{"eliminacion": "Conectar a red de acueducto municipal con agua potable certificada.", "sustitucion": "Instalar sistemas de purificación de agua (filtros, ósmosis inversa, luz UV), suministrar agua embotellada certificada.", "controles_ingenieria": "Tanques de almacenamiento limpios y cubiertos, sistemas de cloración automática, filtros de agua, puntos de lavado de manos con agua potable, señalización clara de agua no potable.", "controles_administrativos": "Análisis periódico de calidad de agua (bacteriológico y fisicoquímico según Resolución 2115 de 2007), limpieza y desinfección de tanques de almacenamiento cada 6 meses, capacitación en riesgos de agua no potable, vacunación (hepatitis A, fiebre tifoidea), prohibición de consumo de agua no potable, suministro de agua potable para consumo y lavado de manos, botiquín con sales de rehidratación oral."}'::jsonb, false, 35, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (220, 23, 'Deslizamientos', NULL, 'Atrapamiento, aplastamiento, fracturas, trauma severo, pérdidas materiales, bloqueo de vías de acceso.', 'Muerte por sepultamiento, lesiones graves por aplastamiento, trauma múltiple, amputaciones traumáticas.', '{"EMO": 1, "PSM": 2}'::jsonb, '["Capacidad de reacción rápida ante emergencias", "Conocimiento de señales de advertencia de deslizamiento", "Capacidad de evacuación rápida", "Capacidad de mantener la calma en situaciones de pánico"]'::jsonb, '["Discapacidades de movilidad que dificulten evacuación rápida (requiere plan especial de evacuación asistida)", "Trastornos de pánico severos", "Condiciones cardiorrespiratorias que se agraven con esfuerzo físico súbito"]'::jsonb, '["Casco de seguridad", "Calzado de seguridad con buen agarre", "Silbato de emergencia", "Linterna personal", "Kit de emergencia personal"]'::jsonb, '{"eliminacion": "Reubicar instalaciones fuera de zonas de alto riesgo de deslizamiento.", "sustitucion": "No aplica (fenómeno natural).", "controles_ingenieria": "Muros de contención, sistemas de drenaje para control de aguas lluvias, revegetalización de taludes, mallas de contención en laderas, canales de desviación de escorrentía, estabilización de taludes con geotextiles, monitoreo de grietas y movimientos del terreno.", "controles_administrativos": "Estudios de suelos y geotécnicos antes de construcción, monitoreo de alertas del IDEAM y autoridades locales (temporada de lluvias), plan de emergencias y evacuación específico para deslizamientos, simulacros periódicos, identificación de rutas de evacuación seguras, señalización de zonas de riesgo, restricción de construcción en zonas de amenaza alta, inspecciones post-lluvias fuertes, capacitación en señales de advertencia (grietas, inclinación de árboles, sonidos subterráneos)."}'::jsonb, false, 64, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (221, 28, 'Trabajos en caliente', NULL, 'Quemaduras, incendios, explosiones, inhalación de humos metálicos, lesiones oculares por radiación UV/IR.', 'Incendio masivo, explosión, quemaduras de tercer grado, muerte por quemaduras o inhalación de humo, pérdidas materiales catastróficas.', '{"EMO": 1, "OPTO": 1}'::jsonb, '["Conocimiento de riesgos de trabajos en caliente (soldadura, corte, esmerilado)", "Capacidad de seguir estrictamente protocolos de seguridad", "Buena agudeza visual", "Función respiratoria adecuada", "Capacidad de reacción ante emergencias"]'::jsonb, '["Enfermedades respiratorias severas (asma, EPOC)", "Enfermedades oculares severas no corregidas", "Dermatitis severa o piel muy sensible a quemaduras", "Claustrofobia (para trabajos en espacios confinados)", "Trastornos de atención que impidan seguir protocolos estrictos"]'::jsonb, '["Careta de soldador con filtro adecuado (DIN 9-13 según proceso)", "Guantes de cuero para soldador", "Delantal de cuero o material ignífugo", "Polainas y mangas de cuero", "Respirador para humos metálicos (si hay ventilación insuficiente)", "Ropa ignífuga (no sintética)", "Calzado de seguridad cerrado"]'::jsonb, '{"eliminacion": "Eliminar trabajos en caliente innecesarios, preferir métodos de unión mecánica cuando sea posible.", "sustitucion": "Utilizar soldadura en frío, adhesivos industriales, uniones roscadas o remachadas en lugar de soldadura.", "controles_ingenieria": "Cabinas de soldadura con extracción localizada de humos, sistemas de ventilación adecuados, cortinas ignífugas para delimitar área de trabajo, extintores tipo ABC accesibles (máximo 15 metros), detectores de humo y alarmas, mangueras contra incendio disponibles.", "controles_administrativos": "Permiso de trabajo en caliente obligatorio (válido solo para el día), inspección previa del área (retirar materiales combustibles 10m a la redonda), vigilante de incendios durante y 30 min después del trabajo, capacitación certificada en trabajos en caliente, inspección de equipos antes de uso, prohibición de trabajos en caliente cerca de tanques de combustible o químicos inflamables, plan de respuesta a incendios, cumplimiento NTC 1461 (prevención de incendios)."}'::jsonb, true, 60, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (222, 21, 'Acoso Laboral (Mobbing) o Discriminación', 'RPS-ACOSO-LAB', 'Ansiedad, depresión, baja autoestima, estrés crónico, desmotivación, trastornos del sueño, somatización.', 'Suicidio, trastorno depresivo mayor, trastorno de estrés postraumático (TEPT), incapacidad laboral permanente.', '{"EMO": 1, "PSM": 1}'::jsonb, '["Capacidad de comunicación asertiva", "Resiliencia emocional", "Habilidades para establecer límites saludables", "Capacidad de trabajar en equipo"]'::jsonb, '["Ninguna - este riesgo puede afectar a cualquier persona independientemente de sus condiciones de salud", "Nota: Víctimas previas de acoso requieren acompañamiento psicológico"]'::jsonb, '["No aplica EPP físico - requiere controles organizacionales y legales"]'::jsonb, '{"eliminacion": "Política de CERO TOLERANCIA al acoso laboral y discriminación (Ley 1010 de 2006, Ley 1482 de 2011). Despido inmediato del acosador.", "sustitucion": "No aplica.", "controles_ingenieria": "Canales de denuncia confidenciales y seguros (línea ética, correo anónimo). Comité de Convivencia Laboral ACTIVO con reuniones mensuales.", "controles_administrativos": "Código de conducta empresarial firmado por todos. Protocolos de investigación rápida (máx 15 días). Sanciones disciplinarias progresivas. Capacitación obligatoria en diversidad, inclusión y liderazgo ético. Protección a denunciantes (no represalias). Acompañamiento psicológico a víctimas."}'::jsonb, false, 62, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (223, 21, 'Acoso Sexual en el Trabajo', 'RPS-ACOSO-SEX', 'Ansiedad severa, depresión, estrés postraumático, vergüenza, culpa, aislamiento social, ausentismo laboral.', 'Trastorno de estrés postraumático complejo, depresión mayor con ideación suicida, abandono del empleo, demandas penales.', '{"EMO": 1, "PSM": 1}'::jsonb, '["Ninguna - cualquier trabajador/a puede ser víctima de acoso sexual", "Capacidad de identificar conductas inapropiadas y denunciarlas"]'::jsonb, '["Ninguna - este riesgo puede afectar a cualquier persona", "Víctimas previas de abuso sexual requieren acompañamiento especializado"]'::jsonb, '["No aplica EPP físico - requiere controles legales y organizacionales"]'::jsonb, '{"eliminacion": "Política de CERO TOLERANCIA al acoso sexual (Ley 1257/2008, Ley 1010/2006). Despido INMEDIATO del acosador. Denuncia penal obligatoria.", "sustitucion": "No aplica.", "controles_ingenieria": "Cámaras de seguridad en áreas comunes (excepto baños/vestidores). Iluminación adecuada en zonas de riesgo. Espacios de trabajo abiertos (evitar oficinas aisladas sin ventanas).", "controles_administrativos": "Protocolo específico de acoso sexual (distinto de acoso laboral general). Capacitación obligatoria anual sobre consentimiento y límites. Investigación confidencial con personal capacitado en género. Protección ABSOLUTA a víctimas (traslado del acosador, no de la víctima). Acompañamiento psicológico y legal. Rutas de denuncia externa (Fiscalía, Comisaría de Familia)."}'::jsonb, false, 63, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (224, 21, 'Burnout / Síndrome de Desgaste Profesional', 'RPS-BURNOUT', 'Agotamiento físico y emocional extremo, despersonalización, cinismo, reducción de eficacia profesional, irritabilidad, insomnio, ansiedad.', 'Incapacidad laboral prolongada (>180 días), depresión mayor, ideación suicida, abandono de la profesión, enfermedades psicosomáticas.', '{"EMO": 1, "PSM": 1}'::jsonb, '["Capacidad de autocuidado y reconocimiento de límites personales", "Habilidades de manejo de estrés", "Red de apoyo social (familia, amigos)", "Capacidad de desconexión laboral"]'::jsonb, '["Depresión no tratada", "Trastornos de ansiedad severos", "Adicción al trabajo (workaholism)", "Falta de límites trabajo-vida personal"]'::jsonb, '["No aplica EPP físico - requiere controles organizacionales"]'::jsonb, '{"eliminacion": "Reducir cargas de trabajo excesivas. Eliminar metas inalcanzables. Prohibir contacto laboral fuera de horario (derecho a la desconexión digital - Ley 2191/2022).", "sustitucion": "Rotación de tareas de alta demanda emocional. Redistribución de cargas entre equipos.", "controles_ingenieria": "Espacios de descanso adecuados (salas de pausa, jardines). Software de gestión de tareas (evitar sobrecarga). Límites técnicos a correos/mensajes fuera de horario.", "controles_administrativos": "Pausas obligatorias cada 2 horas. Vacaciones obligatorias (mín 15 días continuos/año). Programas de apoyo psicológico (EAP - Employee Assistance Program). Capacitación en manejo de estrés y resiliencia. Evaluación periódica con escalas validadas (MBI - Maslach Burnout Inventory). Cultura organizacional de bienestar (no glorificar el exceso de trabajo)."}'::jsonb, true, 64, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (225, 21, 'Violencia de Terceros (Clientes, Pacientes, Público)', 'RPS-VIOL-TERC', 'Estrés agudo, ansiedad anticipatoria, hipervigilancia, miedo al trabajo, trastornos del sueño, agotamiento emocional.', 'Trastorno de estrés postraumático (TEPT), lesiones físicas graves, muerte, incapacidad permanente psicológica.', '{"EMO": 1, "PSM": 1}'::jsonb, '["Capacidad de manejo de situaciones de conflicto", "Comunicación asertiva bajo presión", "Inteligencia emocional", "Capacidad de mantener la calma en crisis"]'::jsonb, '["Trastorno de estrés postraumático no tratado", "Trastornos de ansiedad severos", "Fobias sociales", "Antecedentes de violencia traumática"]'::jsonb, '["Sistemas de alarma personal (botón de pánico)", "Chalecos antibalas (vigilancia, bancos)", "Radios de comunicación", "Cámaras corporales (bodycam)"]'::jsonb, '{"eliminacion": "Eliminar atención presencial en zonas de alto riesgo (atención remota/virtual). Prohibir atención individual sin testigos.", "sustitucion": "Rotación de personal en áreas de alto riesgo. Trabajo en binomios (nunca solo).", "controles_ingenieria": "Barreras físicas (vidrios blindados en taquillas). Botones de pánico con respuesta inmediata. Cámaras de seguridad visibles. Salidas de emergencia accesibles. Salas de espera con aforo controlado. Detectores de metales.", "controles_administrativos": "Protocolo de manejo de crisis (código de violencia). Capacitación en desescalamiento de conflictos. Presencia de seguridad física. Políticas de NO TOLERANCIA a agresiones (denuncia penal inmediata). Acompañamiento psicológico post-incidente. Reporte obligatorio de incidentes (Res. 652/2012 para alto riesgo). Grupos de apoyo entre pares."}'::jsonb, true, 65, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (226, 21, 'Condiciones de Trabajo en Teletrabajo (Aislamiento, Jornadas Difusas)', 'RPS-TELETRA', 'Aislamiento social, soledad laboral, dificultad para desconectar, jornadas extendidas, estrés por disponibilidad permanente, sedentarismo.', 'Burnout, depresión por aislamiento, trastornos del sueño crónicos, ruptura del equilibrio vida-trabajo, enfermedades musculoesqueléticas.', '{"EMO": 1, "PSM": 2}'::jsonb, '["Autodisciplina y gestión del tiempo", "Capacidad de trabajar de manera autónoma", "Habilidades de comunicación virtual", "Separación clara entre espacio laboral y personal"]'::jsonb, '["Condiciones de vivienda inadecuadas (sin espacio privado)", "Aislamiento social previo", "Dificultades de autogestión", "Dependencia de interacción social constante"]'::jsonb, '["Silla ergonómica (suministrada por empleador)", "Escritorio ajustable", "Monitor externo", "Teclado y mouse ergonómicos", "Soporte para portátil"]'::jsonb, '{"eliminacion": "Establecer modelo híbrido (teletrabajo + presencial alternado). Evitar teletrabajo 100% para todos los roles.", "sustitucion": "Ofrecer espacios de coworking cercanos. Días de encuentro presencial obligatorios.", "controles_ingenieria": "Software con límites de conexión (bloqueo automático fuera de horario). Plataformas de comunicación con status visible (disponible/no disponible). Inspecciones virtuales del puesto de trabajo en casa.", "controles_administrativos": "Derecho a la desconexión digital (Ley 2191/2022) - prohibir contacto fuera de horario. Horario definido y respetado. Pausas activas virtuales. Reuniones sociales virtuales (coffee breaks). Auxilio de internet y energía. Capacitación en ergonomía del hogar. Evaluación de riesgos psicosociales en teletrabajo. Espacios de socialización virtual (no solo trabajo). Apoyo para adecuación del hogar."}'::jsonb, true, 66, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (227, 18, 'Asbesto / Amianto (Exposición a Fibras)', 'RQ-ASBESTO', 'Asbestosis (fibrosis pulmonar), placas pleurales, derrame pleural, tos crónica, dificultad respiratoria progresiva.', 'Mesotelioma pleural (cáncer mortal con 95% letalidad), cáncer de pulmón, cáncer de laringe, muerte (20-40 años post-exposición).', '{"EMO": 1}'::jsonb, '["Capacidad de seguir protocolos de seguridad estrictamente", "Conocimiento sobre manejo seguro de asbesto", "Disciplina para uso correcto de EPP", "Conciencia del riesgo vital"]'::jsonb, '["Enfermedades pulmonares preexistentes (EPOC, asma, fibrosis)", "Fumadores activos (potencian riesgo de cáncer 50x)", "Inmunodepresión", "Enfermedades cardiovasculares"]'::jsonb, '["Respirador con filtro P100 (NIOSH) o FFP3 (EN 149)", "Traje Tyvek desechable categoría III tipo 5/6", "Guantes de nitrilo resistentes", "Botas impermeables desechables", "Gafas de seguridad cerradas", "Ducha de descontaminación obligatoria"]'::jsonb, '{"eliminacion": "ELIMINAR TODO ASBESTO (Res. 2844/2007 - prohibido desde 2007). Retirar techos de eternit, tuberías, frenos, empaques. Contratación SOLO con empresas certificadas en remoción de asbesto.", "sustitucion": "Sustituir por materiales libres de asbesto (fibrocemento sin asbesto, tejas PVC, tuberías PVC).", "controles_ingenieria": "Aislamiento TOTAL del área (presión negativa). Sistemas HEPA de filtración de aire. Zona de descontaminación con 3 cámaras. Señalización de peligro carcinógeno. Monitoreo ambiental continuo (<0.1 fibras/cm³).", "controles_administrativos": "Prohibición ABSOLUTA de trabajos con asbesto sin licencia (Res. 2844/2007). Personal certificado en manejo de asbesto. Exámenes médicos anuales de por vida (incluido post-empleo). Registro en SIVESO (Sistema de Vigilancia Epidemiológica). Notificación obligatoria a ARL. Plan de emergencia para liberación accidental. Disposición final en rellenos autorizados únicamente."}'::jsonb, false, 34, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (228, 18, 'Metales Pesados (Plomo, Mercurio, Cromo Hexavalente, Cadmio)', 'RQ-METAL-PES', 'Intoxicación crónica, anemia, neuropatía periférica, daño renal, alteraciones reproductivas, temblor, cambios de personalidad.', 'Insuficiencia renal crónica, encefalopatía (daño cerebral permanente), cáncer (pulmón, riñón), malformaciones congénitas, muerte.', '{"EMO": 1}'::jsonb, '["Capacidad de seguir protocolos de higiene estrictos", "Conocimiento de primeros auxilios en intoxicación", "Disciplina en uso de EPP y lavado de manos"]'::jsonb, '["Insuficiencia renal previa", "Anemia crónica", "Neuropatías", "Embarazo (teratogénicos)", "Planificación de embarazo (hombres y mujeres)"]'::jsonb, '["Respirador con filtro P100 + carbón activado", "Guantes de nitrilo resistentes a químicos", "Traje impermeable", "Botas de seguridad impermeables", "Gafas de seguridad química", "Prohibido comer, beber o fumar en área de trabajo"]'::jsonb, '{"eliminacion": "Eliminar uso de pinturas con plomo (prohibidas). Sustituir baterías de plomo por tecnologías limpias. Eliminar mercurio en termómetros, lámparas (Convenio de Minamata).", "sustitucion": "Pinturas libres de plomo. Soldaduras sin plomo. Procesos sin cromado hexavalente (usar cromado trivalente).", "controles_ingenieria": "Sistemas de ventilación local exhaustiva (LEV). Cabinas cerradas para procesos. Lavamanos automáticos. Duchas de emergencia. Prohibición de comer en área de trabajo.", "controles_administrativos": "Monitoreo biológico trimestral (plombemia, mercurio en orina). Exámenes de función renal semestrales. Rotación de personal (limitar exposición acumulativa). Capacitación en toxicología. Programa de vigilancia epidemiológica. Ropa de trabajo lavada por la empresa (no llevar a casa). Prohibición de trabajadoras embarazadas o en lactancia."}'::jsonb, false, 35, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (229, 18, 'Sustancias Carcinógenas / Mutagénicas (IARC Grupo 1 y 2A)', 'RQ-CARCINO', 'Mutaciones celulares, alteraciones genéticas, tumores benignos, leucemias, linfomas.', 'Cáncer ocupacional (pulmón, vejiga, leucemia, hígado), malformaciones congénitas, muerte prematura.', '{"EMO": 1}'::jsonb, '["Conocimiento profundo sobre manejo de carcinógenos", "Capacidad de seguir protocolos estrictos sin desviaciones", "Conciencia del riesgo vital a largo plazo"]'::jsonb, '["Antecedentes personales o familiares de cáncer", "Inmunodepresión", "Embarazo o planificación de embarazo", "Fumadores (potencian riesgo)", "Exposición previa a radiación"]'::jsonb, '["Respirador de cara completa con filtros específicos", "Traje hermético tipo 3 o superior", "Guantes de nitrilo doble capa", "Botas impermeables", "Ducha obligatoria post-turno"]'::jsonb, '{"eliminacion": "Sustituir SIEMPRE que exista alternativa (principio de precaución). Eliminar benceno, formaldehído, óxido de etileno cuando sea posible.", "sustitucion": "Usar alternativas menos peligrosas (Ej: tolueno en lugar de benceno, alcoholes en lugar de formaldehído).", "controles_ingenieria": "Sistemas cerrados (sin contacto). Cabinas con presión negativa. Extracción localizada. Automatización de procesos. Señalización de CARCINÓGENO visible. Áreas restringidas con acceso controlado.", "controles_administrativos": "Inventario actualizado de carcinógenos (MSDS). Minimizar número de trabajadores expuestos. Minimizar tiempo de exposición (rotación). Monitoreo ambiental mensual. Vigilancia médica de por vida (incluido post-empleo). Registro en SIVESO. Notificación a ARL. Prohibición absoluta de comer, beber, fumar en área. Capacitación específica anual. Plan de contingencia para derrames."}'::jsonb, false, 36, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (230, 18, 'Plaguicidas y Herbicidas (Organofosforados, Carbamatos, Glifosato)', 'RQ-PLAGUIC', 'Intoxicación aguda (náuseas, vómito, salivación, sudoración), cefalea, mareo, visión borrosa, fasciculaciones musculares.', 'Crisis colinérgica (paro respiratorio), neuropatía retardada, cáncer (linfoma, leucemia), alteraciones reproductivas, muerte.', '{"EMO": 1}'::jsonb, '["Capacidad de lectura e interpretación de etiquetas", "Conocimiento de mezclas y dosificación", "Disciplina en uso de EPP", "Conciencia de síntomas de intoxicación"]'::jsonb, '["Enfermedades hepáticas o renales", "Neuropatías", "Asma bronquial", "Embarazo o lactancia", "Enfermedades de la piel (dermatitis)"]'::jsonb, '["Respirador con filtros A2P3 (vapores orgánicos + partículas)", "Traje Tyvek categoría III tipo 4", "Guantes de nitrilo largos", "Botas de PVC altas", "Gafas de seguridad química", "Delantal impermeable"]'::jsonb, '{"eliminacion": "Implementar control biológico de plagas (depredadores naturales). Rotación de cultivos. Variedades resistentes.", "sustitucion": "Usar plaguicidas de baja toxicidad (categoría III o IV OMS). Sustituir organofosforados por piretroides (menor toxicidad). Herbicidas selectivos en lugar de glifosato.", "controles_ingenieria": "Equipos de aplicación calibrados. Boquillas antideriva. Aplicación en horarios de bajo viento (< 10 km/h). Zonas de exclusión (buffer zones). Almacenamiento en bodegas ventiladas con diques de contención.", "controles_administrativos": "Licencia de aplicador de plaguicidas (obligatorio). Capacitación en manejo seguro (Res. 1075/2015 ICA). Monitoreo de colinesterasa basal y trimestral (organofosforados). Exámenes médicos semestrales. Período de reentrada respetado. Registro de aplicaciones (fecha, producto, dosis). Antídotos disponibles (atropina, pralidoxima). Duchas de emergencia en campo. Ropa de trabajo lavada diariamente (no llevar a casa). Prohibición de aplicación por menores de edad y embarazadas."}'::jsonb, true, 37, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (231, 18, 'Atmósferas Explosivas - ATEX (Gases, Vapores, Polvos Combustibles)', 'RQ-ATEX', 'Quemaduras de primer a tercer grado, lesiones por onda expansiva, trauma acústico, inhalación de gases tóxicos.', 'Explosión masiva con muertes múltiples, destrucción de instalaciones, incendio generalizado, contaminación ambiental, cierre de planta.', '{"EMO": 1}'::jsonb, '["Conocimiento profundo de atmósferas explosivas", "Capacidad de reconocer zonas clasificadas (Zona 0, 1, 2)", "Disciplina en uso de equipos certificados ATEX", "Conciencia del riesgo de explosión"]'::jsonb, '["Trastornos que afecten la atención y concentración", "Problemas de audición (dificulta escuchar alarmas)", "Problemas de olfato (dificulta detectar fugas de gas)", "Imprudencia o tendencia a desacatar protocolos"]'::jsonb, '["Ropa antiestática certificada (EN 1149)", "Calzado conductivo o antiestático", "Guantes antiestáticos", "Prohibido uso de materiales sintéticos (nylon, poliéster)", "Prohibido uso de dispositivos electrónicos no ATEX"]'::jsonb, '{"eliminacion": "Eliminar fuentes de ignición (chispas, llamas, superficies calientes >450°C). Eliminar atmósferas explosivas (ventilación, inertización con nitrógeno).", "sustitucion": "Sustituir solventes inflamables por acuosos. Usar procesos en húmedo (evitar polvos suspendidos).", "controles_ingenieria": "Clasificación de zonas ATEX (Zona 0, 1, 2 para gases; 20, 21, 22 para polvos). Equipos eléctricos certificados ATEX (Ex d, Ex e, Ex i). Ventilación forzada continua (>12 renovaciones/hora). Sistemas de detección de gases con alarmas. Puesta a tierra de equipos y tanques. Conexiones equipotenciales. Sistemas de supresión de explosiones. Puertas antiexplosión (blow-out panels).", "controles_administrativos": "Evaluación de riesgos ATEX (Directiva 1999/92/CE adaptada). Señalización de zonas clasificadas. Permisos de trabajo en caliente (hot work permit). Prohibición de fumar (señalización). Capacitación ATEX obligatoria. Inspecciones de equipos eléctricos. Mantenimiento preventivo estricto. Plan de emergencia para explosión. Simulacros anuales. Monitoreo continuo de concentraciones (< 25% LEL)."}'::jsonb, false, 38, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (232, 19, 'COVID-19 / SARS-CoV-2 (Enfermedad Respiratoria Aguda)', 'RBL-COVID19', 'Infección respiratoria aguda, fiebre, tos, dificultad respiratoria, fatiga, pérdida de olfato/gusto, ausentismo laboral (7-14 días).', 'COVID-19 severo con neumonía bilateral, síndrome de dificultad respiratoria aguda (SDRA), secuelas pulmonares, cardíacas y neurológicas (Long COVID), muerte.', '{"EMO": 1}'::jsonb, '["Esquema de vacunación completo COVID-19 (recomendado, no obligatorio)", "Capacidad de uso correcto de EPP respiratorio", "Conocimiento de protocolos de bioseguridad", "Disciplina en higiene de manos"]'::jsonb, '["Inmunodepresión severa (VIH avanzado, quimioterapia activa)", "Enfermedades pulmonares crónicas (EPOC, fibrosis)", "Insuficiencia cardíaca", "Diabetes no controlada", "Obesidad mórbida (IMC > 40)", "Embarazo de alto riesgo"]'::jsonb, '["Mascarilla quirúrgica (mínimo) o N95/FFP2 (alto riesgo)", "Protección ocular (gafas o careta)", "Guantes desechables", "Bata desechable (sector salud)", "Higiene de manos con alcohol glicerinado 60-95%"]'::jsonb, '{"eliminacion": "Teletrabajo para personal de riesgo. Eliminar reuniones presenciales innecesarias (usar videoconferencias).", "sustitucion": "Atención virtual/telemedicina. Entrega de documentos digitales (sin papel).", "controles_ingenieria": "Ventilación natural cruzada (abrir ventanas). Sistemas de ventilación mecánica (HVAC) con filtros HEPA. Distanciamiento físico (mín 2 metros). Barreras físicas (acrílico en atención al público). Dispensadores de alcohol glicerinado. Señalización de aforo máximo.", "controles_administrativos": "Protocolo de bioseguridad actualizado (Res. 350/2022). Tamizaje diario de síntomas (fiebre, tos). Aislamiento inmediato de casos sospechosos. Pruebas diagnósticas (antígeno/PCR) a sintomáticos y contactos. Rastreo de contactos estrechos. Jornadas flexibles (evitar picos de afluencia). Turnos escalonados. Limpieza y desinfección frecuente de superficies. Capacitación en bioseguridad. Campaña de vacunación (recomendada). Esquema híbrido (presencial/teletrabajo). Prohibición de asistir con síntomas. Licencias flexibles."}'::jsonb, true, 36, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (233, 19, 'Hepatitis B / C (Exposición Ocupacional a Sangre y Fluidos)', 'RBL-HEPAT', 'Infección aguda con ictericia, fatiga, náuseas, dolor abdominal. En Hepatitis C: 70% cronifica.', 'Hepatitis crónica, cirrosis hepática, cáncer de hígado (hepatocarcinoma), insuficiencia hepática, trasplante hepático, muerte.', '{"EMO": 1}'::jsonb, '["Vacunación completa Hepatitis B (3 dosis + refuerzo si < 10 UI/mL)", "Conocimiento de protocolos de bioseguridad", "Disciplina en uso de guantes y EPP", "Conocimiento de profilaxis post-exposición"]'::jsonb, '["Hepatitis crónica activa", "Cirrosis hepática", "Insuficiencia hepática", "Inmunodepresión (pobre respuesta a vacuna)", "Uso de drogas intravenosas"]'::jsonb, '["Guantes de nitrilo o látex", "Bata desechable impermeable", "Gafas de seguridad o careta", "Mascarilla quirúrgica", "Contenedores cortopunzantes (guardián)"]'::jsonb, '{"eliminacion": "Eliminar agujas con recapsulado manual (usar contenedores directos). Eliminar uso de agujas cuando existan alternativas (Ej: vías periféricas por puerto).", "sustitucion": "Usar dispositivos con seguridad incorporada (agujas retráctiles, lancetas de seguridad). Sistemas sin agujas para acceso venoso.", "controles_ingenieria": "Contenedores cortopunzantes en todos los puntos de uso (máx 1 metro de distancia). Superficies lavables e impermeables. Lavamanos automáticos. Señalización de riesgo biológico.", "controles_administrativos": "Vacunación obligatoria Hepatitis B a todo personal de riesgo (Res. 412/2000). Verificación de títulos de anticuerpos (> 10 UI/mL). Protocolo de accidente biológico con seguimiento a 6 meses. Profilaxis post-exposición si no inmunizado (inmunoglobulina + vacuna). Reporte obligatorio de pinchazos (ATEL). Capacitación en precauciones estándar. Vigilancia epidemiológica. Pruebas serológicas anuales. Prohibición de recapsular agujas manualmente. Técnica de una sola mano para desechar."}'::jsonb, true, 37, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (234, 19, 'Tuberculosis - TBC (Mycobacterium tuberculosis)', 'RBL-TBC', 'Tos persistente > 2 semanas, fiebre, sudoración nocturna, pérdida de peso, fatiga, hemoptisis.', 'Tuberculosis pulmonar cavitada, tuberculosis miliar (diseminada), tuberculosis multidrogorresistente (MDR-TB), insuficiencia respiratoria, muerte.', '{"EMO": 1}'::jsonb, '["Esquema de vacunación BCG (niñez)", "Capacidad de reconocer síntomas tempranos", "Disciplina en uso de mascarilla N95", "Conocimiento de aislamiento respiratorio"]'::jsonb, '["VIH/SIDA (riesgo 20x mayor de TBC activa)", "Inmunodepresión (biológicos, quimioterapia)", "Diabetes no controlada", "Insuficiencia renal crónica", "Silicosis", "Desnutrición severa", "Alcoholismo"]'::jsonb, '["Mascarilla N95 o FFP2 (obligatoria en contacto con casos)", "Bata desechable", "Guantes", "Protección ocular (gotas de Flügge)"]'::jsonb, '{"eliminacion": "Identificación y tratamiento precoz de casos (búsqueda activa de sintomáticos respiratorios). Aislamiento inmediato de casos sospechosos.", "sustitucion": "No aplica.", "controles_ingenieria": "Ventilación natural cruzada (> 12 renovaciones/hora). Habitaciones de aislamiento con presión negativa (hospitales). Luz ultravioleta germicida (UVGI) en salas de espera. Separación física de pacientes con tos (sala de aislamiento respiratorio).", "controles_administrativos": "Programa de Control de Tuberculosis (Res. 412/2000). Tamizaje anual con PPD o IGRA a personal de salud. Rx tórax basal y anual. Búsqueda activa de sintomáticos respiratorios (BK en esputo). Tratamiento supervisado directamente observado (DOTS). Estudio de contactos. Quimioprofilaxis con isoniacida a expuestos (contactos VIH+). Capacitación en bioseguridad. Reporte obligatorio a Secretaría de Salud. Licencias médicas completas (6 meses de tratamiento). Restricción de labores hasta negativización (3 BK negativos)."}'::jsonb, false, 38, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (235, 25, 'Contacto Eléctrico Directo (Partes Energizadas Expuestas)', 'CS-CONT-ELEC-DIR', 'Quemaduras eléctricas, fibrilación ventricular, tetanización muscular, caídas desde altura por choque.', 'Paro cardiorrespiratorio, quemaduras de tercer grado, amputación de miembros, muerte instantánea.', '{"EMO": 1}'::jsonb, '["Conocimiento de electricidad y riesgos", "Capacidad de verificar ausencia de tensión (Ley de las 5 reglas de oro)", "Licencia de trabajo eléctrico (alta tensión)", "Conciencia del riesgo vital"]'::jsonb, '["Arritmias cardíacas", "Marcapasos cardíaco", "Epilepsia", "Vértigo o mareos", "Trastornos de atención"]'::jsonb, '["Guantes dieléctricos certificados (clase 0 a 4 según voltaje)", "Calzado dieléctrico", "Casco dieléctrico", "Gafas con protección UV (arco eléctrico)", "Herramientas aisladas (1000V)"]'::jsonb, '{"eliminacion": "Desenergizar circuitos antes de trabajar (procedimiento LOTO - Lock Out Tag Out). Trabajo sin tensión (Ley de las 5 reglas de oro).", "sustitucion": "Usar herramientas aisladas. Tensiones reducidas (< 50V AC, < 120V DC).", "controles_ingenieria": "Encerramiento de partes vivas (tableros cerrados). Distancias de seguridad. Interruptores diferenciales (30 mA). Puesta a tierra. Señalización de peligro eléctrico.", "controles_administrativos": "Permiso de trabajo eléctrico. Certificación de electricistas (Res. 90708/2013 RETIE). Procedimiento de las 5 reglas de oro. Capacitación anual. Inspecciones de instalaciones. Simulacros de RCP. Prohibición de trabajar solo en media/alta tensión."}'::jsonb, true, 48, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (236, 25, 'Arco Eléctrico (Flash Arc / Arc Flash)', 'CS-ARCO-ELEC', 'Quemaduras por destello (flash burn), ceguera temporal, quemaduras de tercer grado en cara y manos, lesiones auditivas.', 'Quemaduras de 3er/4to grado en > 50% del cuerpo, muerte por carbonización, explosión de equipos, incendio.', '{"EMO": 1}'::jsonb, '["Conocimiento de cálculo de energía incidente (cal/cm²)", "Capacidad de usar ropa resistente al arco (AR)", "Certificación en seguridad eléctrica avanzada", "Disciplina en uso de EPP certificado"]'::jsonb, '["Marcapasos o dispositivos electrónicos implantados", "Epilepsia fotosensible", "Problemas cardíacos severos", "Claustrofobia (traje de arco completo)"]'::jsonb, '["Ropa resistente al arco (AR) certificada según energía incidente (NFPA 70E)", "Capucha facial de arco (arc-rated face shield)", "Guantes dieléctricos + guantes de cuero exterior", "Ropa interior ignífuga (no sintética)", "Prohibido: materiales sintéticos (se funden en piel)"]'::jsonb, '{"eliminacion": "Trabajo sin tensión (desenergizar). Procedimiento LOTO estricto.", "sustitucion": "Usar equipos con arco controlado (arc-resistant switchgear). Trabajos remotos (robot/cámara).", "controles_ingenieria": "Estudio de arco eléctrico (cálculo de energía incidente). Etiquetado de advertencia en tableros (cal/cm², distancia, EPP requerido). Relés de protección rápidos (< 0.1 seg). Limitadores de corriente. Barreras físicas.", "controles_administrativos": "Política de trabajo sin tensión. Permiso de trabajo en líneas vivas (solo emergencias). Certificación NFPA 70E. Capacitación anual. Análisis de riesgo de arco (arc flash hazard analysis). Inspecciones termográficas. Mantenimiento preventivo estricto. Prohibición de trabajar solo."}'::jsonb, false, 49, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (237, 30, 'Sepultamiento en Excavaciones / Zanjas', 'CS-SEPULT-EXC', 'Atrapamiento parcial, fracturas, politraumatismos, asfixia por compresión torácica, hipotermia.', 'Sepultamiento total con asfixia mecánica, síndrome compartimental (necrosis muscular), amputación de miembros, muerte en minutos.', '{"EMO": 1}'::jsonb, '["Capacidad de reconocer terrenos inestables", "Conocimiento de técnicas de entibado", "Capacidad física para evacuar rápidamente", "Disciplina en seguir protocolos de excavación segura"]'::jsonb, '["Claustrofobia severa", "Enfermedades respiratorias crónicas (EPOC, asma)", "Movilidad reducida", "Trastornos cardiovasculares severos"]'::jsonb, '["Casco de seguridad", "Botas de seguridad con protección", "Chaleco reflectivo", "Radio de comunicación", "Detector de gases (si aplica)"]'::jsonb, '{"eliminacion": "Evitar excavaciones profundas (usar técnicas de superficie, túnel boring).", "sustitucion": "Uso de tablestacas o muros pantalla en lugar de excavación a cielo abierto.", "controles_ingenieria": "Entibado obligatorio en excavaciones > 1.5m (Res. 348/2018). Taludes con pendiente segura (< 45°). Escaleras de acceso/egreso cada 7m. Barreras perimetrales. Sistemas de drenaje para evitar infiltración.", "controles_administrativos": "Estudio de suelos previo. Permiso de excavación. Inspección diaria pre-turno de paredes. Detector de gases en excavaciones profundas. Capacitación en rescate en espacios confinados. Prohibición de materiales/equipos cerca del borde (mín 60cm). Personal de vigilancia en superficie. Plan de emergencia y rescate."}'::jsonb, false, 50, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (238, 30, 'Espacios Confinados (Atmósfera Deficiente en Oxígeno, Gases Tóxicos)', 'CS-ESP-CONF', 'Mareo, desorientación, pérdida de conciencia, asfixia, intoxicación por gases (CO, H2S, metano).', 'Muerte por asfixia en < 3 minutos (deficiencia O₂ < 16%), explosión por gases inflamables, intoxicación masiva.', '{"EMO": 1, "PSM": 2}'::jsonb, '["Capacidad de trabajar en espacios reducidos", "Conocimiento de monitoreo de atmósferas peligrosas", "Uso correcto de equipos de respiración autónoma (ERA/SCBA)", "Disciplina estricta en protocolos de entrada/salida"]'::jsonb, '["Claustrofobia", "Enfermedades respiratorias (asma, EPOC)", "Enfermedades cardiovasculares", "Epilepsia", "Obesidad mórbida (dificulta rescate)"]'::jsonb, '["Equipo de respiración autónoma (SCBA) o línea de aire", "Arnés de rescate con punto dorsal", "Detector multigás personal (O₂, LEL, CO, H₂S)", "Linterna intrínsecamente segura", "Radio de comunicación"]'::jsonb, '{"eliminacion": "Eliminar la necesidad de entrada (uso de robots, cámaras remotas).", "sustitucion": "Modificación de diseño para evitar espacios confinados.", "controles_ingenieria": "Ventilación forzada continua (> 20 renovaciones/hora). Monitoreo continuo de atmósfera (O₂ 19.5-23.5%, LEL < 10%). Sistemas de alarma. Trípodes de rescate con winch. Iluminación adecuada (12V máx).", "controles_administrativos": "Permiso de entrada obligatorio (Res. 2400/1979). Evaluación de atmósfera antes y durante entrada. Personal entrenado en rescate (mínimo 2 fuera, 1 adentro). Comunicación permanente. Prohibición de llamas abiertas. Procedimiento LOTO para aislar fuentes de energía. Plan de rescate con simulacros. Prohibición de entrada en solitario."}'::jsonb, false, 51, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (239, 24, 'Proyección de Partículas Sólidas (Esmerilado, Corte, Demolición)', 'CS-PROY-PART', 'Traumatismos oculares, cuerpos extraños en ojos, laceraciones faciales, contusiones.', 'Pérdida total de visión (ceguera unilateral/bilateral), perforación ocular, enucleación.', '{"EMO": 2, "OPTO": 1}'::jsonb, '["Agudeza visual normal", "Capacidad de uso correcto de protección ocular", "Reflejos y coordinación para evitar impactos", "Conocimiento de zonas de riesgo"]'::jsonb, '["Visión monocular (mayor riesgo si se pierde el ojo funcional)", "Cirugía ocular reciente", "Glaucoma no controlado", "Uso de lentes de contacto (relativo, según EPP)"]'::jsonb, '["Gafas de seguridad con protección lateral (ANSI Z87.1)", "Careta facial completa (operaciones de alto impacto)", "Pantalla facial con gafas debajo (doble protección)"]'::jsonb, '{"eliminacion": "Métodos de corte sin chispa (corte por agua, laser).", "sustitucion": "Uso de herramientas con menor generación de partículas.", "controles_ingenieria": "Guardas de protección en máquinas. Pantallas de aislamiento. Sistemas de extracción localizada. Señalización de zonas de proyección.", "controles_administrativos": "Uso obligatorio de protección ocular (con sanción). Inspección de EPP antes de uso. Reemplazo de protectores rayados/dañados. Delimitación de áreas de trabajo. Prohibición de terceros en zona de proyección."}'::jsonb, true, 52, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (240, 24, 'Atrapamiento en Puntos de Operación de Maquinaria', 'CS-ATRAP-MAQ', 'Laceraciones, fracturas, aplastamiento de dedos/manos, amputaciones parciales.', 'Amputación traumática de miembros superiores, muerte por sangrado masivo o traumatismo craneoencefálico.', '{"AUD": 1, "EMO": 1, "OPTO": 1}'::jsonb, '["Coordinación motora fina y gruesa adecuada", "Tiempo de reacción rápido", "Capacidad de concentración sostenida", "Conocimiento de zonas de peligro en maquinaria"]'::jsonb, '["Neuropatías periféricas (pérdida de sensibilidad en manos)", "Temblor esencial o parkinsonismo", "Trastorno por déficit de atención severo", "Epilepsia no controlada", "Problemas de audición (no escucha alarmas)"]'::jsonb, '["Guardas de seguridad en todos los puntos de operación", "Dispositivos de parada de emergencia (botones, cuerdas)", "Ropa ajustada sin elementos sueltos (prohibido anillos, pulseras)", "Guantes de protección (SOLO si no aumenta riesgo de atrapamiento)"]'::jsonb, '{"eliminacion": "Automatización completa (robots, cobots con sensores).", "sustitucion": "Herramientas manuales en lugar de maquinaria motorizada cuando sea posible.", "controles_ingenieria": "Guardas fijas y móviles certificadas. Sensores de presencia (cortinas de luz). Dispositivos de alimentación automática. Controles bimanuales (requieren dos manos para activar). Sistemas de enclavamiento. Parada de emergencia accesible (< 2 metros).", "controles_administrativos": "Procedimiento de bloqueo/etiquetado (LOTO) para mantenimiento. Capacitación específica por máquina. Inspecciones pre-operacionales. Prohibición de remover guardas. Prohibición de limpieza con máquina en marcha. Permiso de trabajo para mantenimiento. Señalización de puntos de atrapamiento."}'::jsonb, true, 53, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (241, 24, 'Golpes por Objetos Suspendidos (Grúas, Polipastos, Montacargas)', 'CS-GOLP-SUSP', 'Contusiones, fracturas, traumatismo craneoencefálico, aplastamiento.', 'Aplastamiento de extremidades con amputación, traumatismo múltiple mortal, muerte instantánea.', '{"AUD": 1, "EMO": 1, "OPTO": 1}'::jsonb, '["Agudeza visual y percepción espacial adecuadas", "Capacidad auditiva para escuchar alarmas", "Agilidad y capacidad de reacción rápida", "Conocimiento de señales manuales (rigger signals)"]'::jsonb, '["Visión monocular o campo visual reducido", "Hipoacusia severa", "Trastornos del equilibrio (dificulta evasión rápida)", "Movilidad reducida en miembros inferiores"]'::jsonb, '["Casco de seguridad clase E (eléctrico)", "Calzado con puntera de acero", "Chaleco reflectivo de alta visibilidad", "Guantes de protección"]'::jsonb, '{"eliminacion": "Eliminar trabajos bajo cargas suspendidas (rediseño de procesos).", "sustitucion": "Uso de transportadores automáticos en lugar de grúas.", "controles_ingenieria": "Grúas certificadas con limitador de carga. Alarmas sonoras al operar. Barreras físicas en zonas de tránsito de cargas. Señalización luminosa en montacargas. Eslingas y ganchos certificados. Inspección mensual de cables de acero.", "controles_administrativos": "Operadores de grúa/montacargas certificados (NTC 5830). Plan de izaje para cargas > 5 toneladas. Señalero certificado (rigger). Prohibición de paso bajo cargas suspendidas. Delimitación de zona de operación. Inspección diaria pre-operacional. Registro de mantenimiento. Prohibición de transportar personas en implementos."}'::jsonb, true, 54, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (242, 27, 'Trabajo en Alturas sin Líneas de Vida o Puntos de Anclaje', 'CS-ALT-LINVIDA', 'Caída libre desde altura, fracturas múltiples, traumatismo craneoencefálico, lesiones medulares.', 'Caída mortal, paraplejia/tetraplejia, muerte instantánea.', '{"PL": 1, "AUD": 1, "ECG": 1, "GLI": 1, "PST": 2, "EMOA": 1, "OPTO": 1}'::jsonb, '["Certificación en trabajo en alturas vigente (Res. 1409/2012)", "Ausencia de vértigo o miedo a las alturas", "Buena condición física y equilibrio", "Capacidad de uso correcto de arnés y conectores"]'::jsonb, '["Vértigo o acrofobia", "Epilepsia", "Enfermedades cardíacas severas", "Hipertensión no controlada", "Obesidad mórbida (IMC > 40)"]'::jsonb, '["Arnés de cuerpo completo certificado (ANSI Z359)", "Línea de vida retráctil o eslinga con absorbedor de energía", "Punto de anclaje certificado (> 5000 lb / 22 kN)", "Casco con barbuquejo", "Calzado antideslizante"]'::jsonb, '{"eliminacion": "Realizar trabajos a nivel del suelo (usar brazos telescópicos, plataformas elevadoras).", "sustitucion": "Andamios certificados con barandas en lugar de escaleras.", "controles_ingenieria": "Líneas de vida horizontales permanentes. Puntos de anclaje estructurales certificados. Barandas permanentes (altura 1.1m, travesaño intermedio, rodapié). Plataformas de trabajo con piso antideslizante. Redes de seguridad.", "controles_administrativos": "Permiso de trabajo en altura obligatorio (Res. 1409/2012). Certificación de coordinador, entrenador y trabajador. Inspección diaria de EPP. Rescue plan con personal certificado en rescate. Prohibición de trabajo con vientos > 40 km/h o lluvia. Plan de emergencia. Prohibición de arrojar herramientas. Uso de bolsas porta-herramientas."}'::jsonb, true, 55, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (243, 27, 'Puertas de Emergencia Bloqueadas o Salidas Obstruidas', 'CS-EMERG-BLOQ', 'Atrapamiento en emergencias (incendio, sismo, explosión), pánico colectivo, lesiones por aglomeración.', 'Muerte por asfixia en incendio, aplastamiento en evacuación masiva, muerte múltiple.', '{"EMO": 1, "PSM": 2}'::jsonb, '["Conocimiento de rutas de evacuación", "Capacidad de mantener la calma en emergencias", "Movilidad adecuada para evacuar rápidamente", "Participación en simulacros"]'::jsonb, '["Movilidad reducida severa (requiere plan de evacuación asistida)", "Claustrofobia en espacios congestionados", "Trastornos de pánico severos"]'::jsonb, '["No aplica EPP específico", "Señalización fotoluminiscente de salidas", "Iluminación de emergencia obligatoria"]'::jsonb, '{"eliminacion": "Eliminar obstáculos permanentes. Prohibir almacenamiento cerca de salidas.", "sustitucion": "Puertas de apertura rápida (barra antipánico). Salidas múltiples.", "controles_ingenieria": "Señalización de emergencia fotoluminiscente (visible sin luz). Iluminación de emergencia con batería (mín 90 min). Detectores de humo/fuego vinculados a apertura automática. Puertas con barra antipánico (push bar). Ancho mínimo de salida: 1.1m por cada 50 personas.", "controles_administrativos": "Inspecciones semanales de salidas de emergencia. Prohibición absoluta de bloquear puertas/pasillos. Sanción disciplinaria por obstruir salidas. Plan de evacuación actualizado. Simulacros trimestrales. Planos de evacuación visibles. Brigada de emergencia. Mantenimiento de cerraduras antipánico. Capacitación en evacuación. Punto de encuentro externo señalizado."}'::jsonb, true, 56, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (244, 27, 'Pisos Resbaladizos, Irregulares o en Mal Estado', 'CS-PISO-RESB', 'Caídas al mismo nivel, esguinces de tobillo, fracturas de muñeca/cadera, contusiones.', 'Fractura de cadera en adultos mayores (mortalidad 20% al año), traumatismo craneoencefálico, discapacidad permanente.', '{"EMO": 1, "OPTO": 1}'::jsonb, '["Agudeza visual para detectar irregularidades", "Equilibrio y coordinación adecuados", "Uso de calzado apropiado", "Atención al caminar"]'::jsonb, '["Vértigo crónico", "Trastornos del equilibrio (laberintitis, Parkinson)", "Osteoporosis severa (alto riesgo de fractura)", "Hipovisión no corregida", "Neuropatía periférica en pies"]'::jsonb, '["Calzado antideslizante certificado (coeficiente fricción > 0.5)", "Calzado cerrado (no chancletas/sandalias)"]'::jsonb, '{"eliminacion": "Reparación de pisos dañados (grietas, huecos, desniveles). Eliminación de obstáculos.", "sustitucion": "Pisos antideslizantes (porcelanato clase 3 o superior). Revestimientos con textura.", "controles_ingenieria": "Sistemas de drenaje para evitar encharcamientos. Tapetes absorbentes en entradas. Señalización de desniveles. Iluminación adecuada (mín 200 lux). Pasamanos en rampas/escaleras. Cinta antideslizante en escalones.", "controles_administrativos": "Programa de orden y aseo (5S). Inspecciones diarias de pisos. Limpieza inmediata de derrames. Señalización temporal ''Piso Mojado''. Restricción de acceso durante limpieza. Mantenimiento preventivo de pisos. Reporte obligatorio de irregularidades. Política de calzado de seguridad."}'::jsonb, true, 57, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (245, 23, 'Deslizamientos de Tierra / Remoción en Masa', 'RFN-DESLIZ', 'Sepultamiento parcial, fracturas, contusiones, politraumatismos, daño a infraestructura.', 'Sepultamiento total con asfixia, muerte masiva, destrucción total de instalaciones, colapso de vías de acceso.', '{"EMO": 1}'::jsonb, '["Conocimiento de señales de inestabilidad de terreno (grietas, árboles inclinados)", "Capacidad de evacuación rápida", "Entrenamiento en primeros auxilios", "Disciplina en seguir planes de emergencia"]'::jsonb, '["Movilidad reducida (dificulta evacuación)", "Hipoacusia severa (no escucha alarmas)", "Discapacidad visual severa"]'::jsonb, '["Casco de seguridad", "Botas de seguridad", "Radio de comunicación", "Linterna con batería de respaldo"]'::jsonb, '{"eliminacion": "Relocalizar instalaciones fuera de zonas de amenaza alta. Evitar construcción en laderas > 30°.", "sustitucion": "Uso de estructuras temporales/móviles en zonas de amenaza.", "controles_ingenieria": "Estudios geotécnicos. Obras de estabilización (muros de contención, drenajes). Sistemas de monitoreo (inclinómetros, piezómetros). Alarmas tempranas. Vías de evacuación múltiples. Puntos de encuentro seguros.", "controles_administrativos": "Plan de Emergencias específico para deslizamientos (Ley 1523/2012). Mapas de amenaza (IDEAM, SGC). Inspecciones después de lluvias intensas. Capacitación en reconocimiento de señales. Simulacros trimestrales. Coordinación con UNGRD y Bomberos. Prohibición de construcción en zonas de amenaza alta. Seguro de desastres. Protocolos de evacuación. Sistema de alertas tempranas comunitario."}'::jsonb, false, 65, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (246, 23, 'Inundaciones / Crecientes Súbitas', 'RFN-INUND', 'Ahogamiento parcial, hipotermia, lesiones por arrastre, enfermedades transmitidas por agua contaminada, daño a infraestructura.', 'Ahogamiento masivo, desaparición de personas, destrucción total de instalaciones, pérdida de vidas.', '{"EMO": 1}'::jsonb, '["Capacidad de natación (ideal)", "Conocimiento de zonas de riesgo de inundación", "Capacidad de evacuación rápida", "Disciplina en seguir alertas tempranas"]'::jsonb, '["No saber nadar (en zonas de alto riesgo)", "Movilidad reducida severa", "Enfermedades cardiorrespiratorias que impidan esfuerzo físico intenso"]'::jsonb, '["Chaleco salvavidas (en zonas de muy alto riesgo)", "Botas impermeables de caña alta", "Linterna sumergible", "Radio portátil con batería de respaldo", "Silbato de emergencia"]'::jsonb, '{"eliminacion": "Relocalizar instalaciones fuera de zonas inundables. Evitar construcción en rondas hídricas (30m de cuerpos de agua).", "sustitucion": "Construcción elevada sobre pilotes en zonas de riesgo moderado.", "controles_ingenieria": "Sistemas de drenaje pluvial. Muros de contención y diques. Canales de desvío. Sistemas de bombeo. Compuertas automáticas. Sensores de nivel de agua. Alarmas tempranas comunitarias. Rutas de evacuación señalizadas a zonas altas.", "controles_administrativos": "Plan de Emergencias específico para inundaciones (Ley 1523/2012). Monitoreo de pronósticos meteorológicos (IDEAM). Sistema de alertas tempranas (SAT). Coordinación con UNGRD, Bomberos, Defensa Civil. Mapas de amenaza por inundación. Simulacros semestrales. Kit de emergencia (agua, alimentos, botiquín). Seguro contra inundación. Prohibición de construcción en zonas de amenaza alta. Protocolos de evacuación con puntos de encuentro en zonas altas."}'::jsonb, false, 66, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (247, 23, 'Erupciones Volcánicas (Ceniza, Lahares, Flujos Piroclásticos)', 'RFN-VOLCAN', 'Inhalación de ceniza (silicosis aguda), quemaduras, sepultamiento por lahares, colapso de techos por acumulación de ceniza.', 'Muerte masiva por flujos piroclásticos (>700°C), sepultamiento por lahares, destrucción total de poblaciones (ej: Armero 1985).', '{"EMO": 1}'::jsonb, '["Conocimiento de volcanes activos cercanos (Ruiz, Galeras, Huila, Cerro Machín)", "Capacidad de reconocer señales de actividad volcánica", "Disciplina en seguir órdenes de evacuación", "Capacidad física para evacuar rápidamente"]'::jsonb, '["Enfermedades respiratorias crónicas (asma, EPOC)", "Movilidad reducida severa", "Sordera (dificulta escuchar sirenas de evacuación)"]'::jsonb, '["Mascarilla N95 o P100 (ceniza volcánica)", "Gafas de seguridad cerradas", "Ropa de manga larga (protección ceniza)", "Casco de seguridad", "Radio portátil"]'::jsonb, '{"eliminacion": "No construir en zonas de amenaza volcánica alta (radios de influencia de volcanes activos).", "sustitucion": "Relocalizar instalaciones críticas fuera de zonas de amenaza.", "controles_ingenieria": "Techos inclinados (evitar acumulación de ceniza). Filtros HEPA en sistemas de ventilación. Sellado hermético de ventanas/puertas. Almacenamiento de agua potable. Generadores eléctricos de respaldo. Refugios temporales reforzados.", "controles_administrativos": "Monitoreo permanente del Servicio Geológico Colombiano (SGC). Sistema de alertas volcánicas (nivel verde, amarillo, naranja, rojo). Plan de evacuación con rutas alternas. Simulacros anuales. Coordinación con UNGRD. Kit de emergencia (mascarillas, agua, alimentos). Prohibición de acercarse a zonas de exclusión. Protocolos de limpieza de ceniza (humedecer antes de barrer). Suspensión de actividades durante caída de ceniza."}'::jsonb, false, 67, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (248, 23, 'Sismos / Terremotos de Alta Intensidad (≥ 5.5 Richter)', 'RFN-SISMO', 'Caída de objetos, colapso parcial de estructuras, fracturas, traumatismos, pánico colectivo.', 'Colapso total de edificaciones, sepultamiento, muerte masiva, incendios post-terremoto.', '{"EMO": 1}'::jsonb, '["Conocimiento de protocolos de evacuación sísmica (triángulo de la vida, zonas seguras)", "Capacidad de mantener la calma en emergencias", "Movilidad para evacuar o protegerse rápidamente", "Participación en simulacros"]'::jsonb, '["Movilidad reducida severa (requiere plan de evacuación asistida)", "Trastornos de pánico severos", "Sordera (dificulta escuchar instrucciones)"]'::jsonb, '["Casco de seguridad (tener disponible)", "Botiquín de primeros auxilios", "Linterna con batería de respaldo", "Radio portátil"]'::jsonb, '{"eliminacion": "No aplica (sismos son impredecibles en Colombia).", "sustitucion": "No aplica.", "controles_ingenieria": "Diseño sismorresistente de edificaciones (NSR-10). Anclaje de estanterías y equipos pesados. Reforzamiento de estructuras antiguas. Vidrios laminados (no se fragmentan). Zonas seguras internas señalizadas (marcos de puertas, columnas). Rutas de evacuación libres de obstáculos. Sistemas de detección de sismos (alerta temprana). Generadores de emergencia. Reservas de agua.", "controles_administrativos": "Plan de Emergencias sísmico (Ley 1523/2012). Simulacros trimestrales de evacuación. Brigadas de emergencia entrenadas. Inspecciones estructurales anuales (edificios antiguos). Protocolo: Agacharse, Cubrirse, Agarrarse (Drop, Cover, Hold On). Punto de encuentro externo en zona segura. Kit de emergencia (agua, alimentos, botiquín 72 horas). Mapas de amenaza sísmica (zonas de fallas: Romeral, Palestina). Coordinación con Bomberos y Defensa Civil."}'::jsonb, true, 68, true);
INSERT INTO catalogo_ges (id, riesgo_id, nombre, codigo, consecuencias, peor_consecuencia, examenes_medicos, aptitudes_requeridas, condiciones_incompatibles, epp_sugeridos, medidas_intervencion, es_comun, orden, activo) VALUES (249, 23, 'Rayos / Tormentas Eléctricas (Descargas Atmosféricas)', 'RFN-RAYOS', 'Quemaduras eléctricas, paro cardiorrespiratorio, lesiones auditivas (trueno), incendios, daño a equipos electrónicos.', 'Electrocución mortal instantánea, incendios forestales/estructurales, explosión de tanques de combustible.', '{"EMO": 1}'::jsonb, '["Conocimiento de protocolos de protección contra rayos", "Capacidad de reconocer señales de tormenta inminente", "Disciplina en suspender actividades al exterior", "Conocimiento de regla 30-30 (trueno - refugio)"]'::jsonb, '["Marcapasos cardíaco (alto riesgo de falla por descarga cercana)", "Implantes metálicos extensos", "Sordera (no escucha truenos de advertencia)"]'::jsonb, '["No aplica EPP específico durante tormenta (refugiarse es la prioridad)", "Botas dieléctricas (trabajos al aire libre)", "Ropa seca (ropa mojada aumenta conductividad)"]'::jsonb, '{"eliminacion": "Suspender actividades al aire libre durante tormentas.", "sustitucion": "Trabajo en interiores durante temporada de lluvias (abril-mayo, octubre-noviembre).", "controles_ingenieria": "Sistema de protección contra rayos (pararrayos Franklin o tipo ESE - Early Streamer Emission) según NTC 4552. Red de puesta a tierra < 10 ohmios. Supresores de sobretensión (DPS) en tableros eléctricos. Jaula de Faraday en edificaciones críticas. Refugios metálicos (vehículos cerrados, contenedores). Detector de tormentas (mide campo eléctrico).", "controles_administrativos": "Regla 30-30: Si pasan < 30 seg entre relámpago y trueno, refugiarse inmediatamente. Esperar 30 min después del último trueno para salir. Suspensión de trabajos al aire libre durante tormenta. Prohibición de refugio bajo árboles aislados. Alejarse de estructuras metálicas (cercas, postes). No usa